import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Search, Moon, Sun, Download, Star, Certificate } from 'lucide-react';

const CoursesContent = () => {
  // State management
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [activeView, setActiveView] = useState('overview');
  const [selectedLecture, setSelectedLecture] = useState(null);
  const [selectedForumTopic, setSelectedForumTopic] = useState(null);
  const [showAssignmentDetails, setShowAssignmentDetails] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [darkMode, setDarkMode] = useState(false);
  const [filterCategory, setFilterCategory] = useState('all');
  const [showCertificate, setShowCertificate] = useState(false);

  // Sample data (expanded with more details)
  const courses = [
    {
      id: 1,
      name: 'Computer Vision with OpenVINO',
      instructor: 'Dr. Emily Smith',
      progress: 75,
      color: 'bg-intel-blue',
      description: 'Master computer vision techniques using Intel OpenVINO toolkit.',
      credits: 3,
      duration: '12 weeks',
      enrolled: 156,
      rating: 4.8,
      category: 'AI',
      completionDate: null,
      tags: ['Computer Vision', 'OpenVINO', 'Deep Learning']
    },
    // ... (other courses remain similar, add tags and category)
  ];

  // Toggle dark mode
  useEffect(() => {
    document.body.className = darkMode ? 'dark' : '';
  }, [darkMode]);

  // Filter courses based on search and category
  const filteredCourses = courses.filter(course => 
    course.name.toLowerCase().includes(searchQuery.toLowerCase()) &&
    (filterCategory === 'all' || course.category === filterCategory)
  );

  // Render course analytics
  const renderCourseAnalytics = () => {
    const course = courses.find(c => c.id === selectedCourse);
    return (
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
        <motion.div 
          className="bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-700 rounded-lg p-6"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
        >
          <h4 className="text-lg font-semibold text-intel-dark-blue dark:text-intel-light-blue mb-3">Learning Stats</h4>
          <div className="space-y-4">
            <div>
              <p className="text-sm text-gray-600 dark:text-gray-300">Time Spent</p>
              <p className="text-2xl font-bold text-intel-blue dark:text-intel-light-blue">24h 30m</p>
            </div>
            <div>
              <p className="text-sm text-gray-600 dark:text-gray-300">Completion Rate</p>
              <p className="text-2xl font-bold text-intel-blue dark:text-intel-light-blue">{course.progress}%</p>
            </div>
            <div>
              <p className="text-sm text-gray-600 dark:text-gray-300">Achievements</p>
              <div className="flex space-x-2">
                {[...Array(3)].map((_, i) => (
                  <Star key={i} className="w-6 h-6 text-yellow-400 fill-current" />
                ))}
              </div>
            </div>
          </div>
        </motion.div>
        {/* Add more analytics panels */}
      </div>
    );
  };

  // Render certificate
  const renderCertificate = () => {
    const course = courses.find(c => c.id === selectedCourse);
    return (
      <AnimatePresence>
        {showCertificate && (
          <motion.div
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.8 }}
            className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50"
          >
            <div className="bg-white dark:bg-gray-800 p-8 rounded-lg max-w-2xl w-full">
              <Certificate className="w-16 h-16 text-intel-blue dark:text-intel-light-blue mx-auto mb-4" />
              <h3 className="text-2xl font-bold text-center text-intel-dark-blue dark:text-intel-light-blue">
                Certificate of Completion
              </h3>
              <p className="text-center text-gray-600 dark:text-gray-300 mt-2">
                Congratulations on completing {course.name}!
              </p>
              <div className="mt-6 flex justify-center space-x-4">
                <button 
                  className="bg-intel-blue hover:bg-intel-dark-blue text-white px-4 py-2 rounded flex items-center"
                  onClick={() => {/* Download certificate */}}
                >
                  <Download className="w-4 h-4 mr-2" /> Download PDF
                </button>
                <button 
                  className="border border-gray-300 dark:border-gray-600 px-4 py-2 rounded"
                  onClick={() => setShowCertificate(false)}
                >
                  Close
                </button>
              </div>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    );
  };

  // Modified course list rendering with search and filters
  const renderCourseList = () => {
    return (
      <div className="bg-white dark:bg-gray-800 rounded-lg shadow-md p-6">
        <div className="flex justify-between items-center mb-6">
          <div>
            <h2 className="text-2xl font-bold text-intel-dark-blue dark:text-intel-light-blue">My Intel Learning Hub</h2>
            <p className="text-intel-dark-gray dark:text-gray-400">
              Explore cutting-edge courses powered by Intel technologies
            </p>
          </div>
          <div className="flex items-center space-x-4">
            <button 
              onClick={() => setDarkMode(!darkMode)}
              className="p-2 rounded-full bg-gray-100 dark:bg-gray-700"
              aria-label="Toggle dark mode"
            >
              {darkMode ? <Sun className="w-5 h-5" /> : <Moon className="w-5 h-5" />}
            </button>
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
              <input
                type="text"
                placeholder="Search courses..."
                className="pl-10 pr-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
            </div>
            <select
              className="px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700"
              value={filterCategory}
              onChange={(e) => setFilterCategory(e.target.value)}
            >
              <option value="all">All Categories</option>
              <option value="AI">AI & Machine Learning</option>
              <option value="Data">Data Science</option>
              <option value="Vision">Computer Vision</option>
            </select>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredCourses.map(course => (
            <motion.div
              key={course.id}
              className="border border-gray-200 dark:border-gray-700 rounded-lg overflow-hidden hover:shadow-xl transition-shadow duration-300"
              whileHover={{ scale: 1.02 }}
            >
              <div 
                className={`h-32 ${course.color} flex items-center justify-center cursor-pointer relative`}
                onClick={() => handleCourseAction(course.id, 'overview')}
              >
                <h3 className="text-xl font-bold text-white text-center px-4">{course.name}</h3>
                <div className="absolute top-2 right-2 bg-white bg-opacity-20 px-2 py-1 rounded text-white text-xs">
                  <Star className="w-4 h-4 inline mr-1" /> {course.rating}
                </div>
              </div>
              <div className="p-4 bg-white dark:bg-gray-800">
                <div className="mb-3">
                  <p className="text-intel-gray dark:text-gray-300 text-sm">👨‍🏫 {course.instructor}</p>
                  <p className="text-intel-gray dark:text-gray-300 text-sm">⏱️ {course.duration} • 📚 {course.credits} Credits</p>
                  <p className="text-intel-gray dark:text-gray-300 text-sm">👥 {course.enrolled} students</p>
                  <div className="flex flex-wrap gap-2 mt-2">
                    {course.tags.map(tag => (
                      <span key={tag} className="text-xs bg-intel-light-blue text-white px-2 py-1 rounded">
                        {tag}
                      </span>
                    ))}
                  </div>
                </div>
                <div className="mb-4">
                  <div className="flex justify-between items-center mb-1">
                    <span className="text-sm text-gray-600 dark:text-gray-300">Progress</span>
                    <span className="text-sm font-semibold">{course.progress}%</span>
                  </div>
                  <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2">
                    <div 
                      className="bg-intel-blue h-2 rounded-full transition-all duration-500" 
                      style={{ width: `${course.progress}%` }}
                    ></div>
                  </div>
                </div>
                <div className="grid grid-cols-2 gap-2">
                  <button 
                    className="bg-intel-light-blue hover:bg-intel-blue text-white py-2 rounded transition duration-200 flex items-center justify-center"
                    onClick={() => handleCourseAction(course.id, 'overview')}
                  >
                    View Course
                  </button>
                  {course.progress === 100 && (
                    <button 
                      className="bg-green-600 hover:bg-green-700 text-white py-2 rounded transition duration-200 flex items-center justify-center"
                      onClick={() => setShowCertificate(true)}
                    >
                      <Certificate className="w-4 h-4 mr-2" /> Certificate
                    </button>
                  )}
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    );
  };

  // Modified course content rendering
  const renderCourseContent = () => (
    <div>
      {activeView === 'overview' && renderCourseAnalytics()}
      {renderCertificate()}
      {/* ... (other view render functions remain similar but with dark mode support and animations) */}
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-100 dark:bg-gray-900 transition-colors duration-300">
      <div className="container mx-auto p-4 space-y-6">
        {selectedCourse === null ? (
          renderCourseList()
        ) : (
          <motion.div 
            className="bg-white dark:bg-gray-800 rounded-lg shadow-md p-6"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
          >
            <div className="flex items-center mb-6">
              <button 
                onClick={backToCourseList}
                className="mr-4 bg-intel-light-blue hover:bg-intel-blue text-white p-2 rounded-full transition duration-200"
                aria-label="Back to courses"
              >
                ←
              </button>
              <div>
                <h2 className="text-2xl font-bold text-intel-dark-blue dark:text-intel-light-blue">
                  {courses.find(c => c.id === selectedCourse)?.name}
                </h2>
                <p className="text-gray-600 dark:text-gray-300">
                  {courses.find(c => c.id === selectedCourse)?.instructor}
                </p>
              </div>
            </div>
            {renderNavigationTabs()}
            {renderCourseContent()}
          </motion.div>
        )}
      </div>
    </div>
  );
};

export default CoursesContent;
