import React, { useState } from 'react';

const DiscussionsContent = () => {
  // Sample discussions data
  const discussionsData = [
    { 
      id: 1, 
      title: 'Question about OpenVINO models', 
      course: 'Computer Vision',
      author: 'John Smith',
      date: '2023-07-10',
      replies: 3,
      resolved: false
    },
    { 
      id: 2, 
      title: 'Help with engagement analyzer implementation', 
      course: 'Deep Learning',
      author: 'Emily Johnson',
      date: '2023-07-09',
      replies: 5,
      resolved: true
    },
    { 
      id: 3, 
      title: 'Facial landmarks detection accuracy', 
      course: 'Computer Vision',
      author: 'Michael Brown',
      date: '2023-07-08',
      replies: 2,
      resolved: false
    },
    { 
      id: 4, 
      title: 'Gaze estimation model parameters', 
      course: 'Machine Learning',
      author: 'Sarah Davis',
      date: '2023-07-07',
      replies: 4,
      resolved: true
    },
    { 
      id: 5, 
      title: 'Emotions recognition model training', 
      course: 'Deep Learning',
      author: 'Alex Wilson',
      date: '2023-07-06',
      replies: 1,
      resolved: false
    },
  ];

  // Sample private messages
  const privateMessages = [
    {
      id: 1,
      from: 'Dr. Smith',
      course: 'Computer Vision',
      preview: 'About your recent project submission...',
      date: '2023-07-10',
      unread: true
    },
    {
      id: 2,
      from: 'Prof. Johnson',
      course: 'Machine Learning',
      preview: 'Feedback on your engagement analysis...',
      date: '2023-07-08',
      unread: false
    },
    {
      id: 3,
      from: 'Dr. Williams',
      course: 'Deep Learning',
      preview: 'Regarding your question about OpenVINO...',
      date: '2023-07-05',
      unread: false
    }
  ];

  // State for active tab
  const [activeTab, setActiveTab] = useState('forum');

  return (
    <div className="space-y-6">
      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-2xl font-bold text-intel-dark-blue mb-4">Discussions & Doubts</h2>

        {/* Tabs */}
        <div className="border-b border-gray-200 mb-6">
          <nav className="-mb-px flex space-x-8">
            <button
              className={`py-4 px-1 border-b-2 font-medium text-sm ${activeTab === 'forum' ? 'border-intel-blue text-intel-blue' : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'}`}
              onClick={() => setActiveTab('forum')}
            >
              Q&A Forum
            </button>
            <button
              className={`py-4 px-1 border-b-2 font-medium text-sm ${activeTab === 'private' ? 'border-intel-blue text-intel-blue' : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'}`}
              onClick={() => setActiveTab('private')}
            >
              Private Messages
              {privateMessages.some(msg => msg.unread) && (
                <span className="ml-2 inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800">
                  New
                </span>
              )}
            </button>
          </nav>
        </div>

        {/* Forum Content */}
        {activeTab === 'forum' && (
          <div>
            <div className="flex justify-between items-center mb-6">
              <div className="relative">
                <input
                  type="text"
                  placeholder="Search discussions..."
                  className="pl-10 pr-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-intel-blue focus:border-transparent"
                />
                <div className="absolute left-3 top-2.5 text-gray-400">üîç</div>
              </div>

              <button className="bg-intel-blue hover:bg-intel-dark-blue text-white px-4 py-2 rounded transition duration-200">
                New Question
              </button>
            </div>

            <div className="overflow-hidden shadow ring-1 ring-black ring-opacity-5 md:rounded-lg">
              <table className="min-w-full divide-y divide-gray-300">
                <thead className="bg-intel-light-gray">
                  <tr>
                    <th scope="col" className="py-3.5 pl-4 pr-3 text-left text-sm font-semibold text-intel-dark-blue sm:pl-6">Topic</th>
                    <th scope="col" className="px-3 py-3.5 text-left text-sm font-semibold text-intel-dark-blue">Course</th>
                    <th scope="col" className="px-3 py-3.5 text-left text-sm font-semibold text-intel-dark-blue">Author</th>
                    <th scope="col" className="px-3 py-3.5 text-left text-sm font-semibold text-intel-dark-blue">Replies</th>
                    <th scope="col" className="px-3 py-3.5 text-left text-sm font-semibold text-intel-dark-blue">Status</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-gray-200 bg-white">
                  {discussionsData.map((discussion) => (
                    <tr key={discussion.id} className="hover:bg-gray-50 cursor-pointer">
                      <td className="whitespace-nowrap py-4 pl-4 pr-3 text-sm font-medium text-intel-dark-blue sm:pl-6">{discussion.title}</td>
                      <td className="whitespace-nowrap px-3 py-4 text-sm text-gray-500">{discussion.course}</td>
                      <td className="whitespace-nowrap px-3 py-4 text-sm text-gray-500">{discussion.author}</td>
                      <td className="whitespace-nowrap px-3 py-4 text-sm text-gray-500">{discussion.replies}</td>
                      <td className="whitespace-nowrap px-3 py-4 text-sm text-gray-500">
                        <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${discussion.resolved ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'}`}>
                          {discussion.resolved ? 'Resolved' : 'Open'}
                        </span>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}

        {/* Private Messages Content */}
        {activeTab === 'private' && (
          <div>
            <div className="flex justify-between items-center mb-6">
              <h3 className="text-lg font-medium text-intel-dark-blue">Instructor Messages</h3>
              <button className="bg-intel-blue hover:bg-intel-dark-blue text-white px-4 py-2 rounded transition duration-200">
                New Message
              </button>
            </div>

            <div className="space-y-4">
              {privateMessages.map((message) => (
                <div 
                  key={message.id} 
                  className={`border ${message.unread ? 'border-intel-blue' : 'border-gray-200'} rounded-lg p-4 hover:bg-gray-50 cursor-pointer`}
                >
                  <div className="flex justify-between">
                    <h4 className="font-medium text-intel-dark-blue flex items-center">
                      {message.from}
                      {message.unread && (
                        <span className="ml-2 inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800">
                          New
                        </span>
                      )}
                    </h4>
                    <span className="text-sm text-gray-500">{message.date}</span>
                  </div>
                  <p className="text-sm text-gray-600 mt-1">{message.course}</p>
                  <p className="mt-2">{message.preview}</p>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default DiscussionsContent;
