import React, { useState } from 'react';

const CoursesContent = () => {
  // State for selected course and active view
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [activeView, setActiveView] = useState('overview'); // overview, materials, lectures, announcements, assignments, forum

  // Sample courses data
  const courses = [
    { id: 1, name: 'Computer Vision', instructor: 'Dr. Smith', progress: 75, color: 'bg-intel-blue', description: 'Learn about computer vision techniques, image processing, and object detection using OpenVINO models.' },
    { id: 2, name: 'Machine Learning Fundamentals', instructor: 'Prof. Johnson', progress: 60, color: 'bg-intel-dark-blue', description: 'An introduction to machine learning concepts, algorithms, and practical applications.' },
    { id: 3, name: 'Deep Learning', instructor: 'Dr. Williams', progress: 40, color: 'bg-intel-light-blue', description: 'Advanced neural network architectures, training techniques, and applications.' },
    { id: 4, name: 'Data Visualization', instructor: 'Prof. Davis', progress: 90, color: 'bg-green-600', description: 'Techniques and tools for effective data visualization and communication.' },
  ];

  // Sample data for course details
  const courseMaterials = [
    { id: 1, title: 'Introduction to Computer Vision', type: 'PDF', size: '2.4 MB', uploadDate: '2023-09-01' },
    { id: 2, title: 'Image Processing Techniques', type: 'PDF', size: '3.1 MB', uploadDate: '2023-09-08' },
    { id: 3, title: 'Object Detection with OpenVINO', type: 'PDF', size: '4.5 MB', uploadDate: '2023-09-15' },
    { id: 4, title: 'Course Syllabus', type: 'DOCX', size: '1.2 MB', uploadDate: '2023-08-25' },
    { id: 5, title: 'Practice Dataset', type: 'ZIP', size: '156 MB', uploadDate: '2023-09-10' },
  ];

  const courseLectures = [
    { id: 1, title: 'Lecture 1: Introduction to Computer Vision', duration: '45:20', date: '2023-09-01' },
    { id: 2, title: 'Lecture 2: Image Processing Fundamentals', duration: '50:15', date: '2023-09-08' },
    { id: 3, title: 'Lecture 3: Feature Detection and Matching', duration: '48:30', date: '2023-09-15' },
    { id: 4, title: 'Lecture 4: Object Detection Basics', duration: '52:10', date: '2023-09-22' },
  ];

  const courseAssignments = [
    { id: 1, title: 'Assignment 1: Image Filtering', dueDate: '2023-09-15', status: 'Submitted', grade: '92/100' },
    { id: 2, title: 'Assignment 2: Edge Detection', dueDate: '2023-09-22', status: 'In Progress', grade: null },
    { id: 3, title: 'Assignment 3: Feature Extraction', dueDate: '2023-09-29', status: 'Not Started', grade: null },
    { id: 4, title: 'Final Project Proposal', dueDate: '2023-10-06', status: 'Not Started', grade: null },
  ];

  const courseAnnouncements = [
    { id: 1, title: 'Welcome to Computer Vision!', date: '2023-08-25', content: 'Welcome to the course! Please review the syllabus and join our first live session this Friday.' },
    { id: 2, title: 'Assignment 1 Posted', date: '2023-09-01', content: 'The first assignment has been posted. It focuses on image filtering techniques. Due date is September 15th.' },
    { id: 3, title: 'Office Hours Change', date: '2023-09-10', content: 'Office hours will be moved to Thursdays 2-4pm starting next week.' },
    { id: 4, title: 'Midterm Exam Information', date: '2023-09-18', content: 'The midterm exam will cover lectures 1-5 and will be held on October 13th.' },
  ];

  const courseForumTopics = [
    { id: 1, title: 'Question about Gaussian Filters', author: 'Alex Johnson', date: '2023-09-05', replies: 3, lastActivity: '2023-09-06' },
    { id: 2, title: 'Error in Assignment 1 code', author: 'Maria Garcia', date: '2023-09-08', replies: 5, lastActivity: '2023-09-09' },
    { id: 3, title: 'OpenVINO installation issues', author: 'James Wilson', date: '2023-09-12', replies: 7, lastActivity: '2023-09-14' },
    { id: 4, title: 'Additional resources for feature detection?', author: 'Sarah Miller', date: '2023-09-16', replies: 2, lastActivity: '2023-09-16' },
  ];

  // Function to go back to course list
  const backToCourseList = () => {
    setSelectedCourse(null);
    setActiveView('overview');
  };

  // Function to handle button clicks in course card
  const handleCourseAction = (courseId, action) => {
    setSelectedCourse(courseId);
    setActiveView(action);
  };

  // Function to get status color for assignments
  const getStatusColor = (status) => {
    switch(status) {
      case 'Submitted': return 'text-green-600';
      case 'In Progress': return 'text-yellow-600';
      case 'Not Started': return 'text-red-600';
      default: return 'text-gray-600';
    }
  };

  // Render course list view
  const renderCourseList = () => {
    return (
      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-2xl font-bold text-intel-dark-blue mb-4">My Courses</h2>
        <p className="text-intel-dark-gray mb-6">
          Access your course materials, recorded lectures, and assignments.
        </p>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {courses.map(course => (
            <div key={course.id} className="border border-gray-200 rounded-lg overflow-hidden hover:shadow-md transition-shadow duration-300">
              <div 
                className={`h-32 ${course.color} flex items-center justify-center cursor-pointer`}
                onClick={() => handleCourseAction(course.id, 'overview')}
              >
                <h3 className="text-xl font-bold text-white">{course.name}</h3>
              </div>
              <div className="p-4">
                <p className="text-intel-gray mb-2">Instructor: {course.instructor}</p>
                <div className="mb-2">
                  <div className="w-full bg-gray-200 rounded-full h-2">
                    <div 
                      className="bg-intel-blue h-2 rounded-full" 
                      style={{ width: `${course.progress}%` }}
                    ></div>
                  </div>
                  <p className="text-sm text-right mt-1">{course.progress}% complete</p>
                </div>
                <div className="grid grid-cols-2 gap-2 mt-4">
                  <button 
                    className="bg-intel-light-gray hover:bg-gray-300 text-intel-dark-blue py-1 px-2 rounded text-sm transition duration-200"
                    onClick={() => handleCourseAction(course.id, 'materials')}
                  >
                    Course Materials
                  </button>
                  <button 
                    className="bg-intel-light-gray hover:bg-gray-300 text-intel-dark-blue py-1 px-2 rounded text-sm transition duration-200"
                    onClick={() => handleCourseAction(course.id, 'lectures')}
                  >
                    Recorded Lectures
                  </button>
                </div>
                <div className="grid grid-cols-2 gap-2 mt-2">
                  <button 
                    className="bg-intel-light-gray hover:bg-gray-300 text-intel-dark-blue py-1 px-2 rounded text-sm transition duration-200"
                    onClick={() => handleCourseAction(course.id, 'announcements')}
                  >
                    Announcements
                  </button>
                  <button 
                    className="bg-intel-light-gray hover:bg-gray-300 text-intel-dark-blue py-1 px-2 rounded text-sm transition duration-200"
                    onClick={() => handleCourseAction(course.id, 'assignments')}
                  >
                    Assignments
                  </button>
                </div>
                <button 
                  className="w-full bg-intel-blue hover:bg-intel-dark-blue text-white py-2 rounded transition duration-200 mt-3"
                  onClick={() => handleCourseAction(course.id, 'forum')}
                >
                  Q&A Forum
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };

  return (
    <div className="space-y-6">
      {selectedCourse === null ? (
        // Show course list when no course is selected
        renderCourseList()
      ) : (
        // Show course detail view when a course is selected
        <div className="bg-white rounded-lg shadow-md p-6">
          {/* This will be replaced with the detailed course view in the next step */}
          <div className="flex items-center mb-6">
            <button 
              onClick={backToCourseList}
              className="mr-4 bg-intel-light-gray hover:bg-gray-300 text-intel-dark-blue p-2 rounded-full transition duration-200"
            >
              ←
            </button>
            <h2 className="text-2xl font-bold text-intel-dark-blue">
              {courses.find(c => c.id === selectedCourse)?.name}
            </h2>
          </div>
          <p className="text-intel-gray mb-4">
            Selected view: {activeView} (This will be replaced with the actual content)
          </p>
          <button 
            onClick={backToCourseList}
            className="bg-intel-blue hover:bg-intel-dark-blue text-white px-4 py-2 rounded transition duration-200"
          >
            Back to Course List
          </button>
        </div>
      )}
    </div>
  );
};

export default CoursesContent;
