import React from 'react';

const Controls = ({ 
  isMicOn, 
  toggleMic, 
  isCameraOn, 
  toggleCamera, 
  isScreenSharing, 
  toggleScreenShare, 
  isHandRaised, 
  toggleHandRaise, 
  isChatOpen, 
  toggleChat, 
  isParticipantsOpen, 
  toggleParticipants 
}) => {
  return (
    <div className="bg-gray-800 py-4 px-6 flex justify-center items-center space-x-4">
      {/* Microphone control */}
      <button 
        onClick={toggleMic}
        className={`p-3 rounded-full ${isMicOn ? 'bg-gray-600 hover:bg-gray-700' : 'bg-red-600 hover:bg-red-700'}`}
        title={isMicOn ? 'Turn off microphone' : 'Turn on microphone'}
      >
        <i className={`fas ${isMicOn ? 'fa-microphone' : 'fa-microphone-slash'} text-white`}></i>
      </button>

      {/* Camera control */}
      <button 
        onClick={toggleCamera}
        className={`p-3 rounded-full ${isCameraOn ? 'bg-gray-600 hover:bg-gray-700' : 'bg-red-600 hover:bg-red-700'}`}
        title={isCameraOn ? 'Turn off camera' : 'Turn on camera'}
      >
        <i className={`fas ${isCameraOn ? 'fa-video' : 'fa-video-slash'} text-white`}></i>
      </button>

      {/* Screen sharing control */}
      <button 
        onClick={toggleScreenShare}
        className={`p-3 rounded-full ${isScreenSharing ? 'bg-green-600 hover:bg-green-700' : 'bg-gray-600 hover:bg-gray-700'}`}
        title={isScreenSharing ? 'Stop sharing screen' : 'Share screen'}
      >
        <i className="fas fa-desktop text-white"></i>
      </button>

      {/* Hand raise control */}
      <button 
        onClick={toggleHandRaise}
        className={`p-3 rounded-full ${isHandRaised ? 'bg-yellow-600 hover:bg-yellow-700' : 'bg-gray-600 hover:bg-gray-700'}`}
        title={isHandRaised ? 'Lower hand' : 'Raise hand'}
      >
        <i className="fas fa-hand-paper text-white"></i>
      </button>

      {/* Chat toggle */}
      <button 
        onClick={toggleChat}
        className={`p-3 rounded-full ${isChatOpen ? 'bg-intel-blue hover:bg-intel-dark-blue' : 'bg-gray-600 hover:bg-gray-700'}`}
        title="Toggle chat"
      >
        <i className="fas fa-comment-alt text-white"></i>
      </button>

      {/* Participants toggle */}
      <button 
        onClick={toggleParticipants}
        className={`p-3 rounded-full ${isParticipantsOpen ? 'bg-intel-blue hover:bg-intel-dark-blue' : 'bg-gray-600 hover:bg-gray-700'}`}
        title="Toggle participants list"
      >
        <i className="fas fa-users text-white"></i>
      </button>
    </div>
  );
};

export default Controls;
