import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faRobot, 
  faUser, 
  faPaperPlane, 
  faTimes, 
  faTrash, 
  faMicrophone, 
  faSpinner,
  faCamera,
  faVolumeUp,
  faVolumeMute,
  faImage,
  faSearch,
  faChartSimple,
  faComments
} from '@fortawesome/free-solid-svg-icons';

const ChatbotButton = () => {
  // State variables
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [initialized, setInitialized] = useState(false);
  const [isListening, setIsListening] = useState(false);
  const [speechSupported, setSpeechSupported] = useState(false);
  const [isCameraActive, setIsCameraActive] = useState(false);
  const [capturedImage, setCapturedImage] = useState(null);
  const [isPlayingAudio, setIsPlayingAudio] = useState(false);
  const [currentAudioMessage, setCurrentAudioMessage] = useState(null);

  // New state variables for image generation and web scraping
  const [isGeneratingImage, setIsGeneratingImage] = useState(false);
  const [imagePrompt, setImagePrompt] = useState('');
  const [generatedImage, setGeneratedImage] = useState(null);
  const [isSearchingContent, setIsSearchingContent] = useState(false);
  const [searchTopic, setSearchTopic] = useState('');
  const [searchResults, setSearchResults] = useState(null);
  const [isGeneratingGraph, setIsGeneratingGraph] = useState(false);
  const [graphTopic, setGraphTopic] = useState('');
  const [generatedGraph, setGeneratedGraph] = useState(null);
  const [activeTab, setActiveTab] = useState('chat'); // 'chat', 'image', 'search', 'graph'

  // Refs
  const messagesEndRef = useRef(null);
  const recognitionRef = useRef(null);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  // Generate image from text prompt
  const generateImage = async (prompt) => {
    try {
      setIsGeneratingImage(true);
      setError(null);

      // Use the same origin as the current page to avoid CORS issues
      const backendUrl = 'http://localhost:5001';

      console.log('Generating image for prompt:', prompt);

      const response = await fetch(`${backendUrl}/api/chatbot/text-to-image`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          user_id: getUserId(),
          prompt: prompt,
          width: 512,
          height: 512,
          steps: 10
        }),
        mode: 'cors'
      });

      if (!response.ok) {
        if (response.status === 503) {
          throw new Error('Image generation service is not available on this server');
        } else {
          throw new Error(`Server error: ${response.status}`);
        }
      }

      const data = await response.json();

      if (data.success) {
        setGeneratedImage(data.image);

        // Add the generated image to the conversation
        const newMessage = {
          role: 'assistant',
          content: `I've generated an image based on your prompt: "${prompt}"`,
          hasImage: true,
          imageData: data.image
        };

        setMessages([...messages, newMessage]);
      } else {
        throw new Error(data.message || 'Failed to generate image');
      }
    } catch (err) {
      setError('Error: ' + (err.message || 'Failed to generate image'));
      console.error('Error generating image:', err);
    } finally {
      setIsGeneratingImage(false);
      setImagePrompt('');
    }
  };

  // Search for educational content
  const searchEducationalContent = async (topic) => {
    try {
      setIsSearchingContent(true);
      setError(null);

      // Use the same origin as the current page to avoid CORS issues
      const backendUrl = 'http://localhost:5001';

      console.log('Searching educational content for topic:', topic);

      const response = await fetch(`${backendUrl}/api/chatbot/search-content`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          user_id: getUserId(),
          topic: topic
        }),
        mode: 'cors'
      });

      if (!response.ok) {
        if (response.status === 503) {
          throw new Error('Content search service is not available on this server');
        } else {
          throw new Error(`Server error: ${response.status}`);
        }
      }

      const data = await response.json();

      if (data.success) {
        setSearchResults(data);

        // Add the search results to the conversation
        let content = `Here are some educational resources about "${topic}":\n\n`;

        if (data.images && data.images.length > 0) {
          content += `ðŸ“¸ ${data.images.length} relevant images found\n`;
        } else {
          content += "No relevant images found\n";
        }

        if (data.videos && data.videos.length > 0) {
          content += `ðŸŽ¬ ${data.videos.length} educational videos found:\n`;
          data.videos.forEach((video, index) => {
            content += `${index + 1}. ${video.title}\n`;
          });
        } else {
          content += "No educational videos found";
        }

        const newMessage = {
          role: 'assistant',
          content: content,
          hasSearchResults: true,
          searchData: data
        };

        setMessages([...messages, newMessage]);
      } else {
        throw new Error(data.message || 'Failed to search educational content');
      }
    } catch (err) {
      setError('Error: ' + (err.message || 'Failed to search educational content'));
      console.error('Error searching educational content:', err);
    } finally {
      setIsSearchingContent(false);
      setSearchTopic('');
    }
  };

  // Generate flowchart graph
  const generateFlowchartGraph = async (topic) => {
    try {
      setIsGeneratingGraph(true);
      setError(null);

      // Use the same origin as the current page to avoid CORS issues
      const backendUrl = 'http://localhost:5001';

      console.log('Generating flowchart for topic:', topic);

      const response = await fetch(`${backendUrl}/api/chatbot/generate-graph`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          user_id: getUserId(),
          topic: topic
        }),
        mode: 'cors'
      });

      if (!response.ok) {
        if (response.status === 503) {
          throw new Error('Graph generation service is not available on this server');
        } else {
          throw new Error(`Server error: ${response.status}`);
        }
      }

      const data = await response.json();

      if (data.success) {
        setGeneratedGraph(data.image);

        // Add the generated graph to the conversation
        const newMessage = {
          role: 'assistant',
          content: `I've created a flowchart for "${topic}":\n\n${data.text || ''}`,
          hasGraph: true,
          graphData: data.image
        };

        setMessages([...messages, newMessage]);
      } else {
        throw new Error(data.message || 'Failed to generate flowchart');
      }
    } catch (err) {
      setError('Error: ' + (err.message || 'Failed to generate flowchart'));
      console.error('Error generating flowchart:', err);
    } finally {
      setIsGeneratingGraph(false);
      setGraphTopic('');
    }
  };

  // This function has been replaced by stopAudio

  // Use browser's built-in speech synthesis
  const speakWithBrowserTTS = (text) => {
    if (!('speechSynthesis' in window)) {
      console.error('Browser does not support speech synthesis');
      return false;
    }

    // Stop any ongoing speech
    window.speechSynthesis.cancel();

    const utterance = new SpeechSynthesisUtterance(text);

    utterance.onend = () => {
      setIsPlayingAudio(false);
      setCurrentAudioMessage(null);
      console.log('Speech synthesis finished');
    };

    utterance.onerror = (event) => {
      console.error('Speech synthesis error:', event);
      setIsPlayingAudio(false);
      setCurrentAudioMessage(null);
    };

    window.speechSynthesis.speak(utterance);
    return true;
  };

  // Play audio using browser's TTS with OpenVINO optimization when available
  const playAudio = (messageIndex, text) => {
    if (!window.speechSynthesis) {
      console.error('Speech synthesis not supported');
      return;
    }

    // If already playing this message, stop it
    if (currentAudioMessage === messageIndex) {
      stopAudio();
      return;
    }

    // Stop any currently playing audio
    if (isPlayingAudio) {
      window.speechSynthesis.cancel();
    }

    // Set the current audio state
    setIsPlayingAudio(true);
    setCurrentAudioMessage(messageIndex);

    // Create utterance
    const utterance = new SpeechSynthesisUtterance(text);
    utterance.lang = 'en-US';
    utterance.rate = 1.0;
    utterance.pitch = 1.0;

    // Handle utterance events
    utterance.onend = () => {
      setIsPlayingAudio(false);
      setCurrentAudioMessage(null);
    };

    utterance.onerror = (event) => {
      console.error('TTS error:', event);
      setIsPlayingAudio(false);
      setCurrentAudioMessage(null);
    };

    // Play the audio
    window.speechSynthesis.speak(utterance);
  };

  // Stop TTS audio playback
  const stopAudio = () => {
    if (window.speechSynthesis) {
      window.speechSynthesis.cancel();
      setIsPlayingAudio(false);
      setCurrentAudioMessage(null);
    }
  };

  // Stop audio playback (alias for stopAudio for backward compatibility)
  const stopAudioPlayback = () => {
    stopAudio();
  };

  // Get or generate a user ID
  const getUserId = () => {
    let userId = localStorage.getItem('chatbot_user_id');
    if (!userId) {
      userId = 'user_' + Math.random().toString(36).substring(2, 15);
      localStorage.setItem('chatbot_user_id', userId);
    }
    return userId;
  };

  // ... (rest of the code remains the same)
  // Scroll to bottom of messages
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Initialize speech recognition
  useEffect(() => {
    const checkSpeechSupport = () => {
      const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
      if (SpeechRecognition) {
        setSpeechSupported(true);
      }
    };

    checkSpeechSupport();
    setInitialized(true);
  }, []);

  // Handle key press in input field
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  // Toggle speech recognition
  const toggleListening = () => {
    if (isListening) {
      stopListening();
    } else {
      startListening();
    }
  };

  // Start speech recognition
  const startListening = () => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      console.error('Speech recognition not supported');
      return;
    }

    try {
      const recognition = new SpeechRecognition();
      recognition.continuous = false;
      recognition.interimResults = false;
      recognition.lang = 'en-US';

      recognition.onstart = () => {
        setIsListening(true);
        console.log('Speech recognition started');
      };

      recognition.onresult = (event) => {
        const transcript = event.results[0][0].transcript;
        setInputMessage(transcript);
        console.log('Speech recognized:', transcript);

        // Small delay to show the transcript before sending
        setTimeout(() => {
          sendMessage(transcript);
        }, 500);
      };

      recognition.onerror = (event) => {
        console.error('Speech recognition error:', event.error);
        setIsListening(false);
      };

      recognition.onend = () => {
        setIsListening(false);
        console.log('Speech recognition ended');
      };

      recognitionRef.current = recognition;
      recognition.start();
    } catch (error) {
      console.error('Error starting speech recognition:', error);
      setIsListening(false);
    }
  };

  // Stop speech recognition
  const stopListening = () => {
    if (recognitionRef.current) {
      recognitionRef.current.stop();
      recognitionRef.current = null;
    }
    setIsListening(false);
  };

  // Toggle camera
  const toggleCamera = () => {
    if (isCameraActive) {
      stopCamera();
    } else {
      startCamera();
    }
  };

  // Start camera
  const startCamera = async () => {
    try {
      const constraints = {
        video: {
          facingMode: 'environment',
          width: { ideal: 1280 },
          height: { ideal: 720 }
        }
      };

      const stream = await navigator.mediaDevices.getUserMedia(constraints);

      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        setIsCameraActive(true);
        console.log('Camera started');
      }
    } catch (err) {
      console.error('Error starting camera:', err);
      setError('Could not access camera: ' + err.message);
    }
  };

  // Stop camera
  const stopCamera = () => {
    if (videoRef.current && videoRef.current.srcObject) {
      const tracks = videoRef.current.srcObject.getTracks();
      tracks.forEach(track => track.stop());
      videoRef.current.srcObject = null;
    }
    setIsCameraActive(false);
    setCapturedImage(null);
    console.log('Camera stopped');
  };

  // Capture image from camera
  const captureImage = () => {
    if (!videoRef.current || !canvasRef.current) return;

    const video = videoRef.current;
    const canvas = canvasRef.current;

    // Set canvas dimensions to match video
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    // Draw the current video frame to the canvas
    const ctx = canvas.getContext('2d');
    ctx.drawImage(video, 0, 0, canvas.width, canvas.height);

    // Convert canvas to data URL
    const imageDataUrl = canvas.toDataURL('image/jpeg');
    setCapturedImage(imageDataUrl);

    // Stop the camera after capturing
    stopCamera();

    console.log('Image captured');
  };

  // Process captured image with OCR using OpenVINO optimization when available
  const processImage = async () => {
    if (!capturedImage) return;

    try {
      setIsLoading(true);
      setLoadingMessage('Processing image with OCR (OpenVINO optimized)...');

      // Convert base64 image to blob for sending to backend
      const base64Data = capturedImage.split(',')[1];

      const response = await fetch(`${backendUrl}/api/chatbot/image-to-text`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          image: base64Data,
          useOpenVINO: true // Request OpenVINO optimization if available
        }),
      });

      if (!response.ok) {
        throw new Error(`OCR request failed with status: ${response.status}`);
      }

      const data = await response.json();

      // Add the extracted text to the input field
      setInputMessage(data.text);

      // Add performance metrics to messages if available
      if (data.performanceMetrics) {
        const { processingTimeMs, accelerator } = data.performanceMetrics;
        const processingTime = (processingTimeMs / 1000).toFixed(2);
        const performanceMessage = `Image processed in ${processingTime}s using ${accelerator || 'CPU'}`;

        setMessages([
          ...messages,
          {
            role: 'assistant',
            content: `Text extracted from image: "${data.text}"
\n${performanceMessage}`,
            hasPerformanceMetrics: true
          }
        ]);
      }

      // Close camera and clear captured image
      setCapturedImage(null);
      setIsCameraActive(false);

      // Focus on the input field
      if (inputRef.current) {
        inputRef.current.focus();
      }
    } catch (error) {
      console.error('Error processing image:', error);
      setErrorMessage('Failed to process image. Please try again.');

      // Try again without OpenVINO if there was an error
      try {
        setLoadingMessage('Retrying with standard OCR...');
        const base64Data = capturedImage.split(',')[1];

        const response = await fetch(`${backendUrl}/api/chatbot/image-to-text`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ 
            image: base64Data,
            useOpenVINO: false // Fallback to standard implementation
          }),
        });

        if (!response.ok) {
          throw new Error(`OCR request failed with status: ${response.status}`);
        }

        const data = await response.json();
        setInputMessage(data.text);
        setCapturedImage(null);
        setIsCameraActive(false);
      } catch (fallbackError) {
        console.error('Fallback OCR also failed:', fallbackError);
      }
    } finally {
      setIsLoading(false);
    }
  };

  // Send message to chatbot
  const sendMessage = async (text = null) => {
    const messageText = text || inputMessage;
    if (!messageText.trim() || isLoading) return;

    const userMessage = {
      role: 'user',
      content: messageText
    };

    setMessages([...messages, userMessage]);
    setInputMessage('');

    await sendChatbotRequest(messageText);
  };

  // Send request to chatbot API
  const sendChatbotRequest = async (messageText) => {
    try {
      setIsLoading(true);
      setError(null);

      // Add typing indicator
      setMessages(prevMessages => [...prevMessages, { role: 'assistant', isTyping: true }]);

      // Use the same origin as the current page to avoid CORS issues
      const backendUrl = 'http://localhost:5001';

      const response = await fetch(`${backendUrl}/api/chatbot/chat`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          user_id: getUserId(),
          message: messageText
        }),
        mode: 'cors'
      });

      if (!response.ok) {
        throw new Error(`Server error: ${response.status}`);
      }

      const data = await response.json();

      // Remove typing indicator
      setMessages(prevMessages => prevMessages.filter(msg => !msg.isTyping));

      if (data.success) {
        const botMessage = {
          role: 'assistant',
          content: data.response,
          hasAudio: true,
          ttsUnavailable: data.tts_unavailable || false
        };

        setMessages(prevMessages => [...prevMessages, botMessage]);
      } else {
        throw new Error(data.message || 'Failed to get response from chatbot');
      }
    } catch (err) {
      // Remove typing indicator in case of error
      setMessages(prevMessages => prevMessages.filter(msg => !msg.isTyping));

      setError('Error: ' + err.message);
      console.error('Error sending message to chatbot:', err);
    } finally {
      setIsLoading(false);
    }
  };

  // Clear conversation
  const clearConversation = () => {
    setMessages([]);
    setError(null);
    setCapturedImage(null);
    setGeneratedImage(null);
    setSearchResults(null);
    setGeneratedGraph(null);
    stopAudioPlayback();
  };

  // Return JSX
  return (
    <div className="fixed bottom-4 right-4 flex flex-col items-end z-50">
      {isOpen && (
        <div className="bg-white rounded-lg shadow-lg mb-2 w-80 md:w-96 flex flex-col" style={{ height: '500px' }}>
          {/* Header */}
          <div className="flex justify-between items-center p-3 border-b border-gray-200">
            <div className="flex items-center">
              <FontAwesomeIcon icon={faRobot} className="text-intel-blue mr-2" />
              <h3 className="font-medium">AI Assistant</h3>
            </div>
            <div className="flex space-x-2">
              <button 
                onClick={() => setMessages([])} 
                className="text-gray-500 hover:text-gray-700"
                title="Clear conversation"
              >
                <FontAwesomeIcon icon={faTrash} />
              </button>
              <button 
                onClick={() => setIsOpen(false)} 
                className="text-gray-500 hover:text-gray-700"
                title="Close"
              >
                <FontAwesomeIcon icon={faTimes} />
              </button>
            </div>
          </div>

          {/* Tabs */}
          <div className="flex border-b border-gray-200">
            <button
              className={`flex-1 py-2 px-4 text-sm font-medium ${activeTab === 'chat' ? 'text-intel-blue border-b-2 border-intel-blue' : 'text-gray-500 hover:text-gray-700'}`}
              onClick={() => setActiveTab('chat')}
            >
              <FontAwesomeIcon icon={faComments} className="mr-1" />
              Chat
            </button>
            <button
              className={`flex-1 py-2 px-4 text-sm font-medium ${activeTab === 'image' ? 'text-intel-blue border-b-2 border-intel-blue' : 'text-gray-500 hover:text-gray-700'}`}
              onClick={() => setActiveTab('image')}
            >
              <FontAwesomeIcon icon={faImage} className="mr-1" />
              Image
            </button>
            <button
              className={`flex-1 py-2 px-4 text-sm font-medium ${activeTab === 'search' ? 'text-intel-blue border-b-2 border-intel-blue' : 'text-gray-500 hover:text-gray-700'}`}
              onClick={() => setActiveTab('search')}
            >
              <FontAwesomeIcon icon={faSearch} className="mr-1" />
              Search
            </button>
            <button
              className={`flex-1 py-2 px-4 text-sm font-medium ${activeTab === 'graph' ? 'text-intel-blue border-b-2 border-intel-blue' : 'text-gray-500 hover:text-gray-700'}`}
              onClick={() => setActiveTab('graph')}
            >
              <FontAwesomeIcon icon={faChartSimple} className="mr-1" />
              Graph
            </button>
          </div>

          {/* Chat Tab Content */}
          {activeTab === 'chat' && (
            <div className="flex-1 p-4 overflow-y-auto max-h-96 bg-gray-50">
              {messages.length === 0 && !isLoading ? (
                <div className="text-center text-gray-500 py-8">
                  <FontAwesomeIcon icon={faRobot} size="2x" className="mb-2 text-intel-blue" />
                  <p>Start a conversation with the AI assistant</p>
                </div>
              ) : (
                <div className="space-y-4">
                  {messages.map((msg, index) => (
                    <div key={index} className={`flex ${msg.role === 'user' ? 'justify-end' : 'justify-start'}`}>
                      <div className={`${msg.role === 'user' ? 'bg-intel-blue text-white rounded-lg rounded-br-none' : 'bg-gray-200 text-gray-800 rounded-lg rounded-bl-none'} p-3 max-w-3/4 break-words`}>
                        {/* User or assistant icon */}
                        <div className="flex items-center mb-1">
                          <FontAwesomeIcon 
                            icon={msg.role === 'user' ? faUser : faRobot} 
                            className={`mr-1 ${msg.role === 'user' ? 'text-white' : 'text-intel-blue'}`}
                            size="xs"
                          />
                          <span className="text-xs font-medium">
                            {msg.role === 'user' ? 'You' : 'AI Assistant'}
                          </span>
                        </div>

                        {/* Message text content */}
                        <div className="whitespace-pre-wrap">
                          {msg.content}
                        </div>

                        {/* Generated image */}
                        {msg.hasImage && msg.imageData && (
                          <div className="mt-2">
                            <img 
                              src={`data:image/png;base64,${msg.imageData}`} 
                              alt="Generated image" 
                              className="rounded-lg max-w-full h-auto"
                            />
                          </div>
                        )}

                        {/* Search results */}
                        {msg.hasSearchResults && msg.searchData && (
                          <div className="mt-2">
                            {/* Images */}
                            {msg.searchData.images && msg.searchData.images.length > 0 && (
                              <div className="mb-2">
                                <h4 className="text-sm font-medium mb-1">Images:</h4>
                                <div className="grid grid-cols-2 gap-1">
                                  {msg.searchData.images.slice(0, 4).map((image, idx) => (
                                    <div key={idx} className="aspect-square overflow-hidden rounded">
                                      <img 
                                        src={image.url} 
                                        alt={image.alt || 'Search result'} 
                                        className="w-full h-full object-cover"
                                      />
                                    </div>
                                  ))}
                                </div>
                              </div>
                            )}

                            {/* Videos */}
                            {msg.searchData.videos && msg.searchData.videos.length > 0 && (
                              <div>
                                <h4 className="text-sm font-medium mb-1">Videos:</h4>
                                <div className="space-y-1">
                                  {msg.searchData.videos.slice(0, 3).map((video, idx) => (
                                    <a 
                                      key={idx} 
                                      href={video.url} 
                                      target="_blank" 
                                      rel="noopener noreferrer"
                                      className="flex items-center p-1 bg-white bg-opacity-50 rounded hover:bg-opacity-70"
                                    >
                                      <div className="w-16 h-9 bg-gray-300 rounded overflow-hidden flex-shrink-0 mr-2">
                                        {video.thumbnail && (
                                          <img 
                                            src={video.thumbnail} 
                                            alt={video.title} 
                                            className="w-full h-full object-cover"
                                          />
                                        )}
                                      </div>
                                      <div className="text-xs overflow-hidden">
                                        <div className="font-medium truncate">{video.title}</div>
                                        <div className="text-gray-600 truncate">{video.channel || 'Educational video'}</div>
                                      </div>
                                    </a>
                                  ))}
                                </div>
                              </div>
                            )}
                          </div>
                        )}

                        {/* Flowchart graph */}
                        {msg.hasGraph && msg.graphData && (
                          <div className="mt-2">
                            <img 
                              src={`data:image/png;base64,${msg.graphData}`} 
                              alt="Flowchart graph" 
                              className="rounded-lg max-w-full h-auto"
                            />
                          </div>
                        )}

                        {/* Audio playback controls */}
                        {msg.role === 'assistant' && msg.hasAudio && !msg.ttsUnavailable && (
                          <div className="mt-2">
                            <button 
                              onClick={() => playAudio(index, msg.content)}
                              className={`text-xs ${isPlayingAudio && currentAudioMessage === index ? 'text-red-600 hover:text-red-700' : 'text-gray-600 hover:text-gray-700'}`}
                            >
                              <FontAwesomeIcon 
                                icon={isPlayingAudio && currentAudioMessage === index ? faVolumeMute : faVolumeUp} 
                                className="mr-1" 
                              />
                              {isPlayingAudio && currentAudioMessage === index ? 'Stop audio' : 'Listen'}
                            </button>
                          </div>
                        )}

                        {/* Message for when TTS is unavailable */}
                        {msg.role === 'assistant' && msg.ttsUnavailable && (
                          <div className="mt-2">
                            <span className="text-xs text-gray-500 italic">
                              <FontAwesomeIcon icon={faVolumeMute} className="mr-1" />
                              Audio unavailable
                            </span>
                          </div>
                        )}
                      </div>
                    </div>
                  ))}
                  {isLoading && (
                    <div className="flex justify-start">
                      <div className="bg-gray-200 text-gray-800 rounded-lg rounded-bl-none p-3 max-w-3/4">
                        <div className="flex items-center space-x-1">
                          <div className="w-2 h-2 bg-gray-600 rounded-full animate-bounce"></div>
                          <div className="w-2 h-2 bg-gray-600 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                          <div className="w-2 h-2 bg-gray-600 rounded-full animate-bounce" style={{ animationDelay: '0.4s' }}></div>
                        </div>
                      </div>
                    </div>
                  )}
                  <div ref={messagesEndRef} />
                </div>
              )}
            </div>
          )}

          {/* Error Message */}
          {error && (
            <div className="bg-red-100 text-red-800 p-2 text-xs">
              {error}
              <button 
                onClick={() => setError(null)} 
                className="ml-2 text-red-800 hover:text-red-900"
              >
                Dismiss
              </button>
            </div>
          )}

          {/* Camera view */}
          {isCameraActive && (
            <div className="relative border-t border-gray-200 p-2">
              <div className="bg-black rounded-lg" style={{ minHeight: '200px' }}>
                <video
                  ref={videoRef}
                  autoPlay
                  playsInline
                  muted
                  className="w-full h-full rounded-lg"
                  style={{ maxHeight: '200px', objectFit: 'contain' }}
                  onLoadedData={() => console.log('Video loaded and ready')}
                />
              </div>
              <div className="absolute bottom-4 left-0 right-0 flex justify-center">
                <button
                  onClick={captureImage}
                  className="bg-intel-blue hover:bg-blue-700 text-white p-2 rounded-full shadow"
                  title="Capture image"
                >
                  <FontAwesomeIcon icon={faCamera} />
                </button>
              </div>
              <canvas ref={canvasRef} className="hidden" />
            </div>
          )}

          {/* Captured image */}
          {capturedImage && (
            <div className="border-t border-gray-200 p-2">
              <div className="bg-black rounded-lg overflow-hidden" style={{ minHeight: '200px' }}>
                <img
                  src={capturedImage}
                  alt="Captured"
                  className="w-full h-full object-contain"
                  style={{ maxHeight: '200px' }}
                />
              </div>
              <div className="flex justify-center mt-2 space-x-2">
                <button
                  onClick={() => setCapturedImage(null)}
                  className="bg-gray-500 hover:bg-gray-600 text-white px-3 py-1 rounded"
                >
                  Retake
                </button>
                <button
                  onClick={processImage}
                  className="bg-intel-blue hover:bg-blue-700 text-white px-3 py-1 rounded"
                  disabled={isLoading}
                >
                  {isLoading ? (
                    <>
                      <FontAwesomeIcon icon={faSpinner} spin className="mr-1" />
                      Processing...
                    </>
                  ) : (
                    'Process Image'
                  )}
                </button>
              </div>
          </div>
          )}

          {/* Input area */}
          <div className="border-t border-gray-200 p-2">
            {activeTab === 'chat' && (
              <div className="flex items-center">
                <div className="flex-1 relative">
                  <textarea
                    ref={inputRef}
                    value={inputMessage}
                    onChange={(e) => setInputMessage(e.target.value)}
                    placeholder="Type your message..."
                    className="w-full border border-gray-300 rounded-lg py-2 px-3 focus:outline-none focus:ring-2 focus:ring-intel-blue focus:border-transparent resize-none"
                    style={{ minHeight: '44px', maxHeight: '120px' }}
                    rows="1"
                    onKeyDown={(e) => {
                      if (e.key === 'Enter' && !e.shiftKey) {
                        e.preventDefault();
                        sendMessage();
                      }
                    }}
                    disabled={isLoading || isListening}
                  />

                  {/* Microphone button for speech recognition */}
                  {speechSupported && (
                    <button
                      onClick={toggleListening}
                      className={`absolute right-2 bottom-2 p-1 rounded-full ${isListening ? 'text-red-500 bg-gray-100' : 'text-gray-500 hover:text-intel-blue'}`}
                      title={isListening ? 'Stop listening' : 'Start voice input'}
                      disabled={isLoading}
                    >
                      <FontAwesomeIcon icon={isListening ? faStop : faMicrophone} />
                    </button>
                  )}
                </div>

                {/* Camera toggle button */}
                <button
                  onClick={toggleCamera}
                  className={`ml-2 p-2 rounded-full ${isCameraActive ? 'bg-intel-blue text-white' : 'text-gray-500 hover:bg-gray-300 text-gray-700'}`}
                  disabled={isLoading}
                  title={isCameraActive ? 'Close camera' : 'Open camera'}
                >
                  <FontAwesomeIcon icon={faCamera} />
                </button>

                {/* Send button */}
                <button
                  onClick={() => sendMessage()}
                  className="ml-2 bg-intel-blue hover:bg-blue-700 text-white p-2 rounded-full disabled:opacity-50 disabled:cursor-not-allowed"
                  disabled={!inputMessage.trim() || isLoading}
                  title="Send message"
                >
                  {isLoading ? (
                    <FontAwesomeIcon icon={faSpinner} spin />
                  ) : (
                    <FontAwesomeIcon icon={faPaperPlane} />
                  )}
                </button>
              </div>
            )}

            {activeTab === 'image' && (
              <div className="flex flex-col space-y-2">
                <div className="flex items-center">
                  <input
                    type="text"
                    value={imagePrompt}
                    onChange={(e) => setImagePrompt(e.target.value)}
                    placeholder="Describe the image you want to generate..."
                    className="flex-1 border border-gray-300 rounded-lg p-2 mr-2 focus:outline-none focus:ring-2 focus:ring-intel-blue"
                    disabled={isGeneratingImage}
                    onKeyDown={(e) => {
                      if (e.key === 'Enter') {
                        e.preventDefault();
                        generateImage(imagePrompt);
                      }
                    }}
                  />
                  <button
                    onClick={() => generateImage(imagePrompt)}
                    className="bg-intel-blue hover:bg-blue-700 text-white p-2 rounded-lg disabled:opacity-50 disabled:cursor-not-allowed"
                    disabled={!imagePrompt.trim() || isGeneratingImage}
                  >
                    {isGeneratingImage ? (
                      <FontAwesomeIcon icon={faSpinner} spin />
                    ) : (
                      <span>Generate</span>
                    )}
                  </button>
                </div>
                <p className="text-xs text-gray-500">Generate images from text descriptions using AI</p>
              </div>
            )}

            {activeTab === 'search' && (
              <div className="flex flex-col space-y-2">
                <div className="flex items-center">
                  <input
                    type="text"
                    value={searchTopic}
                    onChange={(e) => setSearchTopic(e.target.value)}
                    placeholder="Enter a topic to search for educational content..."
                    className="flex-1 border border-gray-300 rounded-lg p-2 mr-2 focus:outline-none focus:ring-2 focus:ring-intel-blue"
                    disabled={isSearchingContent}
                    onKeyDown={(e) => {
                      if (e.key === 'Enter') {
                        e.preventDefault();
                        searchEducationalContent(searchTopic);
                      }
                    }}
                  />
                  <button
                    onClick={() => searchEducationalContent(searchTopic)}
                    className="bg-intel-blue hover:bg-blue-700 text-white p-2 rounded-lg disabled:opacity-50 disabled:cursor-not-allowed"
                    disabled={!searchTopic.trim() || isSearchingContent}
                  >
                    {isSearchingContent ? (
                      <FontAwesomeIcon icon={faSpinner} spin />
                    ) : (
                      <span>Search</span>
                    )}
                  </button>
                </div>
                <p className="text-xs text-gray-500">Find educational images and videos on any topic</p>
              </div>
            )}

            {activeTab === 'graph' && (
              <div className="flex flex-col space-y-2">
                <div className="flex items-center">
                  <input
                    type="text"
                    value={graphTopic}
                    onChange={(e) => setGraphTopic(e.target.value)}
                    placeholder="Enter a topic for flowchart generation..."
                    className="flex-1 border border-gray-300 rounded-lg p-2 mr-2 focus:outline-none focus:ring-2 focus:ring-intel-blue"
                    disabled={isGeneratingGraph}
                    onKeyDown={(e) => {
                      if (e.key === 'Enter') {
                        e.preventDefault();
                        generateFlowchartGraph(graphTopic);
                      }
                    }}
                  />
                  <button
                    onClick={() => generateFlowchartGraph(graphTopic)}
                    className="bg-intel-blue hover:bg-blue-700 text-white p-2 rounded-lg disabled:opacity-50 disabled:cursor-not-allowed"
                    disabled={!graphTopic.trim() || isGeneratingGraph}
                  >
                    {isGeneratingGraph ? (
                      <FontAwesomeIcon icon={faSpinner} spin />
                    ) : (
                      <span>Generate</span>
                    )}
                  </button>
                </div>
                <p className="text-xs text-gray-500">Create educational flowcharts and diagrams</p>
              </div>
            )}
          </div>
        </div>
      )}

      {/* Chat button */}
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="bg-intel-blue hover:bg-blue-700 text-white rounded-full p-3 shadow-lg flex items-center justify-center"
        aria-label="Open AI Assistant"
      >
        <FontAwesomeIcon icon={faRobot} size="lg" />
      </button>
    </div>
  );
};

export default ChatbotButton;
