import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import PerformanceContent from '../components/teacher/PerformanceContent';
import StudentDashboard from './StudentDashboard';

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Get user from localStorage
    const userData = localStorage.getItem('user');
    if (userData) {
      setUser(JSON.parse(userData));
    } else {
      navigate('/login');
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/login');
  };

  if (!user) return null;

  // Render appropriate dashboard based on user role
  if (user.role === 'teacher') {
    return <TeacherDashboard user={user} onLogout={handleLogout} />;
  } else {
    return <StudentDashboard user={user} onLogout={handleLogout} />;
  }
};

// Teacher Dashboard Component
const TeacherDashboard = ({ user, onLogout }) => {
  return (
    <div className="min-h-screen bg-intel-light-gray">
      <header className="bg-intel-blue shadow-md">
        <div className="container mx-auto px-4 py-4 flex justify-between items-center">
          <div className="flex items-center">
            <h1 className="text-white text-2xl font-bold">CoreMentis</h1>
            <span className="ml-2 text-white/80 text-sm">Teacher Dashboard</span>
          </div>
          <div className="flex items-center">
            <span className="text-white mr-4">Welcome, {user.name || user.email}</span>
            <button
              onClick={onLogout}
              className="bg-white text-intel-blue px-4 py-1 rounded hover:bg-gray-100 transition duration-300"
            >
              Logout
            </button>
          </div>
        </div>
      </header>

      <main className="container mx-auto px-4 py-8">
        <div className="bg-white rounded-lg shadow-md p-6">
          <PerformanceContent />
        </div>
      </main>
    </div>
  );
};



export default Dashboard;
