import React, { useState, useEffect } from 'react';
import { 
  Calendar, 
  Clock, 
  MapPin, 
  Video, 
  Bell, 
  Download, 
  Filter, 
  Search,
  ChevronLeft,
  ChevronRight,
  Plus,
  BookOpen,
  Users,
  AlertCircle,
  CheckCircle,
  Settings,
  Bookmark,
  MessageSquare
} from 'lucide-react';

const ScheduleContent = () => {
  const [currentWeek, setCurrentWeek] = useState(0);
  const [selectedDay, setSelectedDay] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterType, setFilterType] = useState('all');
  const [notifications, setNotifications] = useState([]);
  const [upcomingClasses, setUpcomingClasses] = useState([]);
  const [viewMode, setViewMode] = useState('week'); // week, day, month

  // Sample schedule data with enhanced information
  const days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday'];
  const timeSlots = ['9:00 AM', '10:30 AM', '12:00 PM', '2:00 PM', '3:30 PM', '5:00 PM'];

  const schedule = [
    { 
      id: 1,
      day: 'Monday', 
      time: '10:30 AM', 
      course: 'Computer Vision', 
      instructor: 'Dr. Sarah Chen',
      room: 'Room 301', 
      color: 'bg-blue-600',
      type: 'lecture',
      duration: '90 min',
      meetingLink: 'https://meet.google.com/cv-class',
      materials: ['Slides', 'Assignment 3'],
      attendance: 'mandatory',
      status: 'scheduled'
    },
    { 
      id: 2,
      day: 'Monday', 
      time: '2:00 PM', 
      course: 'Data Visualization Lab', 
      instructor: 'Prof. Mike Johnson',
      room: 'Lab 2', 
      color: 'bg-green-600',
      type: 'lab',
      duration: '120 min',
      meetingLink: 'https://meet.google.com/dv-lab',
      materials: ['Lab Manual', 'Dataset'],
      attendance: 'mandatory',
      status: 'scheduled'
    },
    { 
      id: 3,
      day: 'Tuesday', 
      time: '9:00 AM', 
      course: 'Deep Learning', 
      instructor: 'Dr. Alex Rodriguez',
      room: 'Room 405', 
      color: 'bg-purple-600',
      type: 'lecture',
      duration: '90 min',
      meetingLink: 'https://meet.google.com/dl-class',
      materials: ['Neural Networks PDF', 'Code Examples'],
      attendance: 'mandatory',
      status: 'scheduled'
    },
    { 
      id: 4,
      day: 'Wednesday', 
      time: '12:00 PM', 
      course: 'Machine Learning', 
      instructor: 'Dr. Lisa Wang',
      room: 'Room 201', 
      color: 'bg-indigo-600',
      type: 'lecture',
      duration: '90 min',
      meetingLink: 'https://meet.google.com/ml-class',
      materials: ['ML Textbook Ch. 5', 'Practice Problems'],
      attendance: 'mandatory',
      status: 'scheduled'
    },
    { 
      id: 5,
      day: 'Thursday', 
      time: '3:30 PM', 
      course: 'Computer Vision Lab', 
      instructor: 'Dr. Sarah Chen',
      room: 'Lab 3', 
      color: 'bg-blue-600',
      type: 'lab',
      duration: '120 min',
      meetingLink: 'https://meet.google.com/cv-lab',
      materials: ['OpenCV Tutorial', 'Assignment 4'],
      attendance: 'mandatory',
      status: 'scheduled'
    },
    { 
      id: 6,
      day: 'Friday', 
      time: '10:30 AM', 
      course: 'AI Ethics Seminar', 
      instructor: 'Prof. David Kim',
      room: 'Room 405', 
      color: 'bg-orange-600',
      type: 'seminar',
      duration: '60 min',
      meetingLink: 'https://meet.google.com/ai-ethics',
      materials: ['Ethics Paper', 'Discussion Topics'],
      attendance: 'optional',
      status: 'scheduled'
    },
  ];

  // Assignments and deadlines
  const assignments = [
    { id: 1, course: 'Computer Vision', title: 'Object Detection Project', due: '2025-07-05', priority: 'high' },
    { id: 2, course: 'Deep Learning', title: 'Neural Network Implementation', due: '2025-07-07', priority: 'medium' },
    { id: 3, course: 'Machine Learning', title: 'Classification Report', due: '2025-07-10', priority: 'low' },
  ];

  // Mock current time for demo
  const getCurrentTime = () => {
    return new Date().toLocaleTimeString('en-US', { 
      hour: 'numeric', 
      minute: '2-digit',
      hour12: true 
    });
  };

  const getClassForTimeSlot = (day, time) => {
    return schedule.find(item => item.day === day && item.time === time);
  };

  const getFilteredSchedule = () => {
    let filtered = schedule;

    if (filterType !== 'all') {
      filtered = filtered.filter(item => item.type === filterType);
    }

    if (searchTerm) {
      filtered = filtered.filter(item => 
        item.course.toLowerCase().includes(searchTerm.toLowerCase()) ||
        item.instructor.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    return filtered;
  };

  const joinClass = (classInfo) => {
    // Simulate joining a class
    window.open(classInfo.meetingLink, '_blank');
  };

  const downloadSchedule = () => {
    // Simulate schedule download
    const scheduleData = JSON.stringify(schedule, null, 2);
    const blob = new Blob([scheduleData], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'my-schedule.json';
    a.click();
    URL.revokeObjectURL(url);
  };

  const syncWithGoogleCalendar = () => {
    // Simulate Google Calendar sync
    alert('Syncing with Google Calendar... This would integrate with Google Calendar API in a real application.');
  };

  const toggleNotification = (classId) => {
    // Toggle notification for a specific class
    setNotifications(prev => 
      prev.includes(classId) 
        ? prev.filter(id => id !== classId)
        : [...prev, classId]
    );
  };

  const getWeekDates = () => {
    const today = new Date();
    const startOfWeek = new Date(today);
    startOfWeek.setDate(today.getDate() - today.getDay() + 1 + (currentWeek * 7));

    return days.map((day, index) => {
      const date = new Date(startOfWeek);
      date.setDate(startOfWeek.getDate() + index);
      return {
        day,
        date: date.getDate(),
        month: date.toLocaleDateString('en-US', { month: 'short' })
      };
    });
  };

  const weekDates = getWeekDates();

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
          <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between">
            <div className="mb-4 lg:mb-0">
              <h1 className="text-3xl font-bold text-gray-900 mb-2">Weekly Schedule</h1>
              <p className="text-gray-600">Your personalized timetable and academic calendar</p>
            </div>

            <div className="flex flex-wrap gap-3">
              <button
                onClick={syncWithGoogleCalendar}
                className="flex items-center gap-2 bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition duration-200"
              >
                <Calendar className="w-4 h-4" />
                Sync Calendar
              </button>

              <button
                onClick={downloadSchedule}
                className="flex items-center gap-2 bg-gray-600 text-white px-4 py-2 rounded-lg hover:bg-gray-700 transition duration-200"
              >
                <Download className="w-4 h-4" />
                Export
              </button>

              <button className="flex items-center gap-2 bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700 transition duration-200">
                <Settings className="w-4 h-4" />
                Settings
              </button>
            </div>
          </div>
        </div>

        {/* Controls */}
        <div className="bg-white rounded-lg shadow-sm p-4 mb-6">
          <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between gap-4">
            {/* Week Navigation */}
            <div className="flex items-center gap-3">
              <button
                onClick={() => setCurrentWeek(prev => prev - 1)}
                className="p-2 rounded-lg border border-gray-300 hover:bg-gray-50 transition duration-200"
              >
                <ChevronLeft className="w-5 h-5" />
              </button>

              <span className="text-lg font-semibold px-4">
                Week {currentWeek === 0 ? 'Current' : currentWeek > 0 ? `+${currentWeek}` : currentWeek}
              </span>

              <button
                onClick={() => setCurrentWeek(prev => prev + 1)}
                className="p-2 rounded-lg border border-gray-300 hover:bg-gray-50 transition duration-200"
              >
                <ChevronRight className="w-5 h-5" />
              </button>
            </div>

            {/* Search and Filter */}
            <div className="flex gap-3">
              <div className="relative">
                <Search className="w-5 h-5 absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
                <input
                  type="text"
                  placeholder="Search courses..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>

              <select
                value={filterType}
                onChange={(e) => setFilterType(e.target.value)}
                className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              >
                <option value="all">All Classes</option>
                <option value="lecture">Lectures</option>
                <option value="lab">Labs</option>
                <option value="seminar">Seminars</option>
              </select>
            </div>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
          {/* Main Schedule */}
          <div className="lg:col-span-3">
            <div className="bg-white rounded-lg shadow-sm overflow-hidden">
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead className="bg-gray-50">
                    <tr>
                      <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Time
                      </th>
                      {weekDates.map(({ day, date, month }) => (
                        <th key={day} className="px-4 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                          <div className="flex flex-col items-center">
                            <span>{day}</span>
                            <span className="text-lg font-bold text-gray-900">{date}</span>
                            <span className="text-xs text-gray-400">{month}</span>
                          </div>
                        </th>
                      ))}
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {timeSlots.map(time => (
                      <tr key={time} className="hover:bg-gray-50">
                        <td className="px-4 py-6 whitespace-nowrap text-sm font-medium text-gray-900">
                          <div className="flex items-center gap-2">
                            <Clock className="w-4 h-4 text-gray-400" />
                            {time}
                          </div>
                        </td>
                        {days.map(day => {
                          const classInfo = getClassForTimeSlot(day, time);
                          const isFiltered = classInfo && getFilteredSchedule().includes(classInfo);

                          return (
                            <td key={`${day}-${time}`} className="px-2 py-6 whitespace-nowrap">
                              {classInfo && isFiltered && (
                                <div className={`${classInfo.color} text-white p-3 rounded-lg shadow-sm hover:shadow-md transition duration-200 cursor-pointer`}>
                                  <div className="flex items-start justify-between mb-2">
                                    <div className="flex-1">
                                      <h4 className="font-semibold text-sm leading-tight">{classInfo.course}</h4>
                                      <p className="text-xs opacity-90 mt-1">{classInfo.instructor}</p>
                                    </div>
                                    <button
                                      onClick={() => toggleNotification(classInfo.id)}
                                      className="text-white hover:text-yellow-200 transition duration-200"
                                    >
                                      <Bell className={`w-4 h-4 ${notifications.includes(classInfo.id) ? 'fill-current' : ''}`} />
                                    </button>
                                  </div>

                                  <div className="space-y-1 text-xs">
                                    <div className="flex items-center gap-1">
                                      <MapPin className="w-3 h-3" />
                                      <span>{classInfo.room}</span>
                                    </div>
                                    <div className="flex items-center gap-1">
                                      <Clock className="w-3 h-3" />
                                      <span>{classInfo.duration}</span>
                                    </div>
                                  </div>

                                  <div className="flex gap-2 mt-3">
                                    <button
                                      onClick={() => joinClass(classInfo)}
                                      className="flex-1 bg-white bg-opacity-20 hover:bg-opacity-30 text-white text-xs py-1 px-2 rounded transition duration-200 flex items-center justify-center gap-1"
                                    >
                                      <Video className="w-3 h-3" />
                                      Join
                                    </button>
                                    <button className="bg-white bg-opacity-20 hover:bg-opacity-30 text-white text-xs py-1 px-2 rounded transition duration-200">
                                      <BookOpen className="w-3 h-3" />
                                    </button>
                                  </div>
                                </div>
                              )}
                            </td>
                          );
                        })}
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>

          {/* Sidebar */}
          <div className="space-y-6">
            {/* Upcoming Classes */}
            <div className="bg-white rounded-lg shadow-sm p-4">
              <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center gap-2">
                <Clock className="w-5 h-5" />
                Today's Classes
              </h3>
              <div className="space-y-3">
                {schedule.filter(item => item.day === 'Monday').map(item => (
                  <div key={item.id} className="flex items-center gap-3 p-3 bg-gray-50 rounded-lg">
                    <div className={`w-3 h-3 rounded-full ${item.color}`}></div>
                    <div className="flex-1">
                      <p className="font-medium text-sm">{item.course}</p>
                      <p className="text-xs text-gray-500">{item.time}  {item.room}</p>
                    </div>
                    <button
                      onClick={() => joinClass(item)}
                      className="text-blue-600 hover:text-blue-800 transition duration-200"
                    >
                      <Video className="w-4 h-4" />
                    </button>
                  </div>
                ))}
              </div>
            </div>

            {/* Assignments */}
            <div className="bg-white rounded-lg shadow-sm p-4">
              <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center gap-2">
                <BookOpen className="w-5 h-5" />
                Upcoming Assignments
              </h3>
              <div className="space-y-3">
                {assignments.map(assignment => (
                  <div key={assignment.id} className="p-3 border border-gray-200 rounded-lg">
                    <div className="flex items-start justify-between mb-2">
                      <h4 className="font-medium text-sm">{assignment.title}</h4>
                      <span className={`text-xs px-2 py-1 rounded-full ${
                        assignment.priority === 'high' ? 'bg-red-100 text-red-800' :
                        assignment.priority === 'medium' ? 'bg-yellow-100 text-yellow-800' :
                        'bg-green-100 text-green-800'
                      }`}>
                        {assignment.priority}
                      </span>
                    </div>
                    <p className="text-xs text-gray-500 mb-2">{assignment.course}</p>
