import React from 'react';
import { useNavigate } from 'react-router-dom';

const ScheduleContent = () => {
  const navigate = useNavigate();
  // Sample schedule data
  const days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday'];
  const timeSlots = ['9:00 AM', '10:30 AM', '12:00 PM', '2:00 PM', '3:30 PM'];

  const schedule = [
    { day: 'Monday', time: '10:30 AM', course: 'Computer Vision', room: 'Room 301', color: 'bg-intel-blue' },
    { day: 'Monday', time: '2:00 PM', course: 'Data Visualization', room: 'Lab 2', color: 'bg-green-600' },
    { day: 'Tuesday', time: '9:00 AM', course: 'Deep Learning', room: 'Room 405', color: 'bg-intel-light-blue' },
    { day: 'Wednesday', time: '12:00 PM', course: 'Machine Learning', room: 'Room 201', color: 'bg-intel-dark-blue' },
    { day: 'Thursday', time: '3:30 PM', course: 'Computer Vision', room: 'Lab 3', color: 'bg-intel-blue' },
    { day: 'Friday', time: '10:30 AM', course: 'Deep Learning', room: 'Room 405', color: 'bg-intel-light-blue' },
  ];

  const getClassForTimeSlot = (day, time) => {
    return schedule.find(item => item.day === day && item.time === time);
  };

  return (
    <div className="space-y-6">
      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-2xl font-bold text-intel-dark-blue mb-4">Weekly Schedule</h2>
        <p className="text-intel-dark-gray mb-6">
          Your personalized timetable for the current week.
        </p>

        {/* Calendar sync button */}
        <div className="mb-6">
          <button className="bg-intel-blue hover:bg-intel-dark-blue text-white px-4 py-2 rounded flex items-center">
            <span className="mr-2">ðŸ“…</span> Sync with Google Calendar
          </button>
        </div>

        {/* Weekly schedule table */}
        <div className="overflow-x-auto">
          <table className="min-w-full border-collapse">
            <thead>
              <tr>
                <th className="border p-2 bg-intel-light-gray">Time</th>
                {days.map(day => (
                  <th key={day} className="border p-2 bg-intel-light-gray">{day}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {timeSlots.map(time => (
                <tr key={time}>
                  <td className="border p-2 font-medium">{time}</td>
                  {days.map(day => {
                    const classInfo = getClassForTimeSlot(day, time);
                    return (
                      <td key={`${day}-${time}`} className="border p-2">
                        {classInfo ? (
                          <div className={`${classInfo.color} text-white p-2 rounded`}>
                            <p className="font-medium">{classInfo.course}</p>
                            <p className="text-sm">{classInfo.room}</p>
                            {/* Show Join button if class is now (simplified logic for demo) */}
                            <button 
                              onClick={() => navigate(`/student/classroom/${classInfo.course.toLowerCase().replace(/\s+/g, '-')}`, { 
                                state: { courseName: classInfo.course } 
                              })}
                              className="mt-1 bg-white text-intel-dark-blue px-2 py-1 rounded text-xs hover:bg-gray-100 transition duration-200"
                            >
                              Join Now
                            </button>
                          </div>
                        ) : null}
                      </td>
                    );
                  })}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default ScheduleContent;
