import React, { useState } from 'react';

const AssignmentsContent = () => {
  // Sample assignments data
  const assignmentsData = [
    { id: 1, title: 'Computer Vision Project', course: 'Computer Vision', dueDate: '2023-07-15', status: 'pending', priority: 'high' },
    { id: 2, title: 'ML Quiz', course: 'Machine Learning', dueDate: '2023-07-18', status: 'pending', priority: 'medium' },
    { id: 3, title: 'Data Visualization Report', course: 'Data Visualization', dueDate: '2023-07-20', status: 'pending', priority: 'medium' },
    { id: 4, title: 'Neural Networks Lab', course: 'Deep Learning', dueDate: '2023-07-10', status: 'completed', priority: 'high' },
    { id: 5, title: 'Research Paper Review', course: 'Computer Vision', dueDate: '2023-07-05', status: 'completed', priority: 'low' },
    { id: 6, title: 'Group Presentation', course: 'Machine Learning', dueDate: '2023-07-25', status: 'pending', priority: 'high' },
  ];

  // Filter states
  const [statusFilter, setStatusFilter] = useState('all');
  const [courseFilter, setCourseFilter] = useState('all');
  const [priorityFilter, setPriorityFilter] = useState('all');

  // Get unique courses for filter dropdown
  const courses = [...new Set(assignmentsData.map(item => item.course))];

  // Apply filters
  const filteredAssignments = assignmentsData.filter(assignment => {
    return (statusFilter === 'all' || assignment.status === statusFilter) &&
           (courseFilter === 'all' || assignment.course === courseFilter) &&
           (priorityFilter === 'all' || assignment.priority === priorityFilter);
  });

  // Status badge color
  const getStatusBadgeClass = (status) => {
    switch(status) {
      case 'completed': return 'bg-green-100 text-green-800';
      case 'pending': return 'bg-yellow-100 text-yellow-800';
      case 'overdue': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  // Priority badge color
  const getPriorityBadgeClass = (priority) => {
    switch(priority) {
      case 'high': return 'bg-red-100 text-red-800';
      case 'medium': return 'bg-yellow-100 text-yellow-800';
      case 'low': return 'bg-blue-100 text-blue-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <div className="space-y-6">
      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-2xl font-bold text-intel-dark-blue mb-4">Assignments</h2>
        <p className="text-intel-dark-gray mb-6">
          Track and manage all your assignments across courses.
        </p>

        {/* Filters */}
        <div className="flex flex-wrap gap-4 mb-6">
          <div>
            <label htmlFor="status-filter" className="block text-sm font-medium text-intel-gray mb-1">Status</label>
            <select
              id="status-filter"
              className="border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-intel-blue"
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
            >
              <option value="all">All Statuses</option>
              <option value="pending">Pending</option>
              <option value="completed">Completed</option>
              <option value="overdue">Overdue</option>
            </select>
          </div>

          <div>
            <label htmlFor="course-filter" className="block text-sm font-medium text-intel-gray mb-1">Course</label>
            <select
              id="course-filter"
              className="border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-intel-blue"
              value={courseFilter}
              onChange={(e) => setCourseFilter(e.target.value)}
            >
              <option value="all">All Courses</option>
              {courses.map(course => (
                <option key={course} value={course}>{course}</option>
              ))}
            </select>
          </div>

          <div>
            <label htmlFor="priority-filter" className="block text-sm font-medium text-intel-gray mb-1">Priority</label>
            <select
              id="priority-filter"
              className="border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-intel-blue"
              value={priorityFilter}
              onChange={(e) => setPriorityFilter(e.target.value)}
            >
              <option value="all">All Priorities</option>
              <option value="high">High</option>
              <option value="medium">Medium</option>
              <option value="low">Low</option>
            </select>
          </div>
        </div>

        {/* Assignments list */}
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-intel-light-gray">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-intel-dark-blue uppercase tracking-wider">Assignment</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-intel-dark-blue uppercase tracking-wider">Course</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-intel-dark-blue uppercase tracking-wider">Due Date</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-intel-dark-blue uppercase tracking-wider">Status</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-intel-dark-blue uppercase tracking-wider">Priority</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-intel-dark-blue uppercase tracking-wider">Action</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {filteredAssignments.map((assignment) => (
                <tr key={assignment.id}>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="font-medium text-intel-dark-blue">{assignment.title}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">{assignment.course}</td>
                  <td className="px-6 py-4 whitespace-nowrap">{assignment.dueDate}</td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${getStatusBadgeClass(assignment.status)}`}>
                      {assignment.status.charAt(0).toUpperCase() + assignment.status.slice(1)}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${getPriorityBadgeClass(assignment.priority)}`}>
                      {assignment.priority.charAt(0).toUpperCase() + assignment.priority.slice(1)}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <button className="text-intel-blue hover:text-intel-dark-blue">
                      View
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {filteredAssignments.length === 0 && (
          <div className="text-center py-4 text-intel-gray">
            No assignments match your filters.
          </div>
        )}
      </div>
    </div>
  );
};

export default AssignmentsContent;
