import React from 'react';

/**
 * Component to display engagement metrics
 */
const EngagementIndicator = ({ engagementData, isVisible }) => {
  if (!isVisible || !engagementData) return null;

  const { engagement_score, trend } = engagementData;

  // Determine color based on engagement score
  const getScoreColor = (score) => {
    if (score >= 70) return 'bg-green-500';
    if (score >= 40) return 'bg-yellow-500';
    return 'bg-red-500';
  };

  // Determine icon based on trend
  const getTrendIcon = (trend) => {
    switch (trend) {
      case 'increasing':
        return '↑';
      case 'decreasing':
        return '↓';
      default:
        return '→';
    }
  };

  return (
    <div className="absolute top-4 right-4 bg-intel-dark-blue bg-opacity-80 text-white p-3 rounded-lg shadow-lg z-50">
      <div className="text-center mb-2">
        <span className="text-sm font-medium">Engagement Level</span>
      </div>

      <div className="flex items-center justify-between mb-1">
        <span className="text-xs">{engagement_score}%</span>
        <span className="text-xs">{getTrendIcon(trend)}</span>
      </div>

      {/* Progress bar */}
      <div className="w-32 h-2 bg-gray-700 rounded-full overflow-hidden">
        <div 
          className={`h-full ${getScoreColor(engagement_score)}`}
          style={{ width: `${engagement_score}%` }}
        ></div>
      </div>

      {/* Engagement level label */}
      <div className="text-center mt-1">
        <span className="text-xs">
          {engagement_score >= 70 ? 'High' : 
           engagement_score >= 40 ? 'Medium' : 'Low'}
        </span>
      </div>
    </div>
  );
};

export default EngagementIndicator;
